// JQuery-plugin-slider
var id1 = $('#slider1');
var id2 = $('#slider2');
var id3 = $('#slider3');
slider(id1);
slider(id2);
slider(id3);

function slider(idSlider){

    var elWrap = idSlider,
        el =  elWrap.find('.img'),
        indexImg = 1,
        indexMax = el.length,
        phase = 3000;

    function change () {
        el.fadeOut(700);
        el.filter(':nth-child('+indexImg+')').fadeIn(700);
    }

    elWrap.append('<span class="next"></span><span class="prev"></span>');
    var btnNext = $('span.next'),
        btnPrev = $('span.prev');

    btnNext.on('click', function() {
        indexImg++;
        if(indexImg > indexMax) {
            indexImg = 1;
        }
        change ();
    });
    btnPrev.on('click', function() {
        indexImg--;
        if(indexImg < 1) {
            indexImg = indexMax;
        }
        change ();
    });
}



